// Instantiate an SPI object (MOSI, MISO, SCK)
static NRF52SPI spi(MOSI, uBit.io.P16, SCK);

/*
	Configure codal SPI settings.
*/
static void initSPI() {
    uBit.serial.printf("Initialising SPI...\r\n");
    // Change the polarity of the SPI interface (1-4) (POLARITY | PHASE)
    spi.setMode(0,8); //CPOL = 0, CPHA = 0, 8 bits per frame?
    // Change the speed of the SPI interface in Hz
    spi.setFrequency(2000000);//2000000, MSBFIRST, SPI_MODE0
} 

/*
	Proxy wait function...
*/
static void delay_ms(int ms) {
  uBit.sleep(ms);
}
 
/*
	Send data over SPI.
*/
static void spiWrite(uint8_t value, uint8_t is_command) {
    DC.setDigitalValue(is_command); //LOW for command (Note 1.5-2)
    CS.setDigitalValue(0);
    spi.write(value);
    CS.setDigitalValue(1);
}
 
/*
	Send data over SPI in COMMAND MODE (0)
*/
static void spiSendCommand(uint8_t value) {
    spiWrite(value, 0);
}
 
/*
	Send data over SPI in DATA MODE (1)
*/
static void spiSendData(uint8_t value) {
    spiWrite(value, 1);
}
 
/*
	Configure microbit expander pins
*/
static void initPins() {
    //Initialise the pins
    uBit.serial.printf("Initialising pins...\r\n");
    DC.setDigitalValue(1);
    CS.setDigitalValue(1);
    RST.setDigitalValue(1);
    PWR.setDigitalValue(1);

    PWR.setHighDrive(true);
    BUSY.setPull(PullMode::None);

    BUSY.getDigitalValue(); //Set pin as input
}
 
/*
	BUSY WAIT LOOP
	Stops control flow while display is processing.
*/
static void readBusy() {
    delay_ms(100);
    uBit.serial.printf("Waiting for Busy...\r\n");
    while (BUSY.getDigitalValue()){
        delay_ms(10);
    }
    // delay_ms(10);
    uBit.serial.printf("Busy Release!\r\n");
}

/*
	HARDWARE RESET
	Toggle RESET pins on display.
*/
static void resetDisplay(){
    uBit.serial.printf("Resetting display...\r\n");
    //Panel Reset
    RST.setDigitalValue(1);
    delay_ms(200);
    RST.setDigitalValue(0);
    delay_ms(2);
    RST.setDigitalValue(1);
    delay_ms(200);
}
/*
	Activate refresh sequence (FULL and SLOW)
*/
static void turnOnDisplay(){
    uBit.serial.printf("Turning on display...\r\n");
    spiSendCommand(0x20);
    readBusy();
}

/*
	Activate partial refresh sequence (slightly FASTER)
	Will mean that previous image is slightly visible at the expense of speed.
	Partial refreshes SHOULD NOT BE EXCLUSIVELY used - can cause DAMAGE as -
	the screen needs to operate its entire sequence to avoid dead/persistent pixels...
*/
static void turnOnDisplayPartial(){
	uBit.serial.printf("Turning on partially...\r\n");
	spiSendCommand(0x22);
	spiSendData(0xc7); // 0xC0 fast, quality: 0xff
	spiSendCommand(0x20);
	readBusy();
}

static void clear(){
    uBit.serial.printf("Clearing...\r\n");
    uint16_t width;
    uint16_t height;
    width = (RES_WIDTH % 8 == 0)? (RES_WIDTH / 8 ): (RES_WIDTH / 8 + 1);
    height = RES_HEIGHT;

    spiSendCommand(0x24);
    for (uint16_t i = 0; i < height; i++) {
        for (uint16_t j = 0; j < width; j++) {
            spiSendData(0xFF);
        }
    }

    spiSendCommand(0x26);
    for (uint16_t i = 0; i < height; i++) {
        for (uint16_t j = 0; j < width; j++) {
            spiSendData(0xFF);
        }
    }

    turnOnDisplay();
}

static void sleep(){
    // spiSendCommand(0x22); //POWER OFF
    // spiSendData(0xC3);
    // spiSendCommand(0x20);

    spiSendCommand(0x10); //enter deep sleep
    spiSendData(0x01);
    delay_ms(100);
}

/*
	Set sub window for drawing, update a portion of screen...
*/
static void setWindow(uint16_t xStart, uint16_t yStart, uint16_t xEnd, uint16_t yEnd){
    spiSendCommand(0x44); // set RAM x address start/end
    spiSendData((xStart >> 3) & 0xFF);
    spiSendData((xEnd >> 3) & 0xFF);

    spiSendCommand(0x45); // set RAM y address start/end
    spiSendData(yStart & 0xFF);
    spiSendData((yStart >> 8) & 0xFF);
    spiSendData(yEnd & 0xFF);
    spiSendData((yEnd >> 8) & 0xFF);
}

/*
	Set window area to draw within.
	Used for Partial Update given dimensions.
*/
static void setCursor(uint16_t xStart, uint16_t yStart)
{
    spiSendCommand(0x4E); // SET_RAM_X_ADDRESS_COUNTER
    spiSendData(xStart & 0xFF);

    spiSendCommand(0x4F); // SET_RAM_Y_ADDRESS_COUNTER
    spiSendData(yStart & 0xFF);
    spiSendData((yStart >> 8) & 0xFF);
}

/*
	Prepare display for writing to it.
	Used for waking the display up after calling sleep()
*/
static void initialise(){   // initialise epaper register
    resetDisplay();

    readBusy();
    spiSendCommand(0x12);  //   SWRESET
    readBusy();

    spiSendCommand(0x01); //Driver output control
    spiSendData(0xf9);
    spiSendData(0x00);
    spiSendData(0x00);   

    spiSendCommand(0x11); //data entry mode
    spiSendData(0x03);

    setWindow(0, 0, RES_WIDTH-1, RES_HEIGHT-1);
    setCursor(0, 0);

    spiSendCommand(0x3C); //BorderWavefrom
    spiSendData(0x05);

    spiSendCommand(0x21); //  Display update control
    spiSendData(0x80);
    spiSendData(0x80);

    // spiSendCommand(0x18); //Read built-in temperature sensor
    // spiSendData(0x80);

    readBusy();
}

/*	
	BLACK IMAGE ONLY
	Send image buffer in RAM to display
	blackImage: black image buffer
*/
void displayBlack(uint8_t *blackImage){{
        uint16_t width;
        uint16_t height;
        width = (RES_WIDTH % 8 == 0)? (RES_WIDTH / 8 ): (RES_WIDTH / 8 + 1);
		height = RES_HEIGHT;

        spiSendCommand(0x24); // write RAM for black/white
		// send data for whole screen
        for (int i = 0; i < ((RES_HEIGHT*RES_WIDTH)/8) + 190; i++) {
            spiSendData(blackImage[i]);
        }

        spiSendCommand(0x26);
		// clear red memory, otherwise whatever was there before is loaded
        for (uint16_t j = 0; j < width; j++) {
            for (uint16_t i = 0; i < height; i++) {
                spiSendData(0xFF);
            }
        }

        turnOnDisplay();
    }
}

/* 
	RED IMAGE ONLY
	Send image buffer in RAM to display
	redImage: red image buffer
*/
void displayRed(uint8_t *redImage){{
        uint16_t width;
        uint16_t height;
        width = (RES_WIDTH % 8 == 0)? (RES_WIDTH / 8 ): (RES_WIDTH / 8 + 1);
		height = RES_HEIGHT;

        spiSendCommand(0x24); 
		// clear black memory, otherwise whatever was loaded there before is reloaded
		for (uint16_t j = 0; j < width; j++) {
            for (uint16_t i = 0; i < height; i++) {
                spiSendData(0xFF);
            }
        }
        
        spiSendCommand(0x26);
        for (int i = 0; i < ((RES_HEIGHT*RES_WIDTH)/8) + 190; i++) {
            spiSendData(redImage[i]);
        }

        // start update sequence (load image from ram)
        turnOnDisplay();
    }
}

/*
	Faster update sequence for black images (white background).
*/
void displayBlackPartial(uint8_t *image){
	resetDisplay();
	readBusy();

	spiSendCommand(0x3C);
	spiSendData(0x80);

	spiSendCommand(0x01); //Driver output control
    spiSendData(0xf9);
    spiSendData(0x00);
    spiSendData(0x00);

	spiSendCommand(0x11);
    spiSendData(0x03); 

	setWindow(0, 0, RES_WIDTH-1, RES_HEIGHT-1);
    setCursor(0, 0);

	spiSendCommand(0x24);
	for (int i = 0; i < ((RES_HEIGHT*RES_WIDTH)/8) + 190; i++) {
		spiSendData(image[i]);
	}

	turnOnDisplayPartial();
}

/*
	EXAMPLE Flow of Operations
	Shows hard coded example image (uint8_t *SAMPLE_IMAGE[])
*/
static void example() {
    PWR.setDigitalValue(0);
	
	// initialise sequence (ran once as setup))
    initPins();
    initSPI();
    initialise();	// call this to wakeup display (sleep())...
    
    displayRed(SAMPLE_IMAGE);	// red example

	uBit.sleep(2000);

	displayBlack(SAMPLE_IMAGE);	// red example

	uBit.sleep(2000);

	uBit.serial.printf("Partially updating...\r\n");
	displayBlackPartial(IMAGE_DATA_CAT);	// partial example (in black)

	sleep();	// sleep display - good practice to do this after every update
    delay_ms(100);
    PWR.setDigitalValue(0);
}
 
int main() {
    uBit.init();
  
    uBit.sleep(1000);
 
    uBit.serial.printf("\r\nPowering up e-ink Display...\r\n");
 
    example();
}